Ext.define('TeShopifyExt.view.ProductWindow', {
    extend: 'Ext.panel.Panel',
    alias: 'widget.productwindow',

    requires: [
        'TeShopifyExt.view.ProductForm'
    ],

    autoScroll: true,
    layout: {
        align: 'stretch',
        type: 'vbox'
    },
    bodyBorder: true,

    initComponent: function() {
        var me = this;

        Ext.applyIf(me, {
            items: [
                {
                    xtype: 'container',
                    style: 'borderBottom: 1px solid #ededed; backgroundColor: #fafafa;',
                    layout: {
                        align: 'stretch',
                        type: 'hbox'
                    },
                    items: [
                        {
                            xtype: 'toolbar',
                            flex: 1,
                            height: 52,
                            style: 'borderBottom: 1px solid #ededed; backgroundColor: #fafafa;',
                            items: [
                                {
                                    xtype: 'button',
                                    margin: '0 0 0 20',
                                    iconCls: 'tags',
                                    scale: 'medium',
                                    text: 'Products',
                                    listeners: {
                                        click: {
                                            fn: me.onButtonClick2,
                                            scope: me
                                        }
                                    }
                                },
                                {
                                    xtype: 'button',
                                    disabled: true,
                                    id: 'ProductButton',
                                    margin: '0 0 0 5',
                                    scale: 'large',
                                    text: 'New Product'
                                },
                                {
                                    xtype: 'tbfill'
                                },
                                {
                                    xtype: 'button',
                                    scale: 'medium',
                                    text: 'Cancel',
                                    listeners: {
                                        click: {
                                            fn: me.onButtonClick,
                                            scope: me
                                        }
                                    }
                                },
                                {
                                    xtype: 'button',
                                    margin: '0 20 0 0',
                                    scale: 'medium',
                                    text: 'Save',
                                    listeners: {
                                        click: {
                                            fn: me.onButtonClick3,
                                            scope: me
                                        }
                                    }
                                }
                            ]
                        }
                    ]
                },
                {
                    xtype: 'productform',
                    flex: 1
                }
            ]
        });

        me.callParent(arguments);
    },

    onButtonClick2: function(button, e, eOpts) {
        var main = Ext.getCmp('centralPanel');
        main.removeAll();
        var panel = Ext.widget('productsview');
        main.add(panel);
    },

    onButtonClick: function(button, e, eOpts) {
        var main = Ext.getCmp('centralPanel');
        main.removeAll();
        var panel = Ext.widget('productsview');
        main.add(panel);
    },

    onButtonClick3: function(button, e, eOpts) {
        var panel = Ext.getCmp('ProductForm');
        var form = panel.getForm();
        var record = form.getRecord();
        var store = Ext.StoreMgr.lookup('ProductStore');
        var id = panel.down('hiddenfield[name="id"]').getValue();
        if (form.isValid()) {
            panel.setLoading('Saving ... ');
            if ( id == 0) {
                store.insert(0, record);
            } 
            form.updateRecord(record);
            store.sync();
            panel.setLoading(false);
        }   
    }

});