Ext.define('TeShopifyExt.view.ShopifyShopWindow', {
    extend: 'Ext.window.Window',
    alias: 'widget.shopifyshopwindow',

    border: 0,
    height: 500,
    id: 'UserWindow1',
    width: 600,
    layout: {
        type: 'fit'
    },
    title: 'Shopify Shop',
    modal: true,

    initComponent: function() {
        var me = this;

        Ext.applyIf(me, {
            items: [
                {
                    xtype: 'form',
                    border: false,
                    id: 'ShopifyForm',
                    padding: 20,
                    layout: {
                        align: 'stretch',
                        type: 'vbox'
                    },
                    url: '/purchase/order/create',
                    items: [
                        {
                            xtype: 'textfield',
                            margins: '0 20 0 0 ',
                            fieldLabel: 'Shopify Shop Name',
                            labelAlign: 'top',
                            name: 'name',
                            allowBlank: false
                        },
                        {
                            xtype: 'textfield',
                            margins: '0 20 0 0 ',
                            fieldLabel: 'Shopify Shop Url',
                            labelAlign: 'top',
                            labelClsExtra: 'x-form-item-label-te',
                            name: 'uri',
                            allowBlank: false
                        },
                        {
                            xtype: 'textfield',
                            margin: '0 20 0 0',
                            fieldLabel: 'Api Key',
                            labelAlign: 'top',
                            labelClsExtra: 'x-form-item-label-te',
                            name: 'apikey',
                            allowBlank: false
                        },
                        {
                            xtype: 'textfield',
                            margin: '0 20 0 0',
                            fieldLabel: 'Password',
                            labelAlign: 'top',
                            labelClsExtra: 'x-form-item-label-te',
                            name: 'password',
                            allowBlank: false
                        },
                        {
                            xtype: 'textfield',
                            margin: '0 20 0 0',
                            fieldLabel: 'Shared Secret',
                            labelAlign: 'top',
                            labelClsExtra: 'x-form-item-label-te',
                            name: 'sharedsecret',
                            allowBlank: false
                        },
                        {
                            xtype: 'textareafield',
                            margin: '0 20 0 0 ',
                            fieldLabel: 'Description',
                            labelAlign: 'top',
                            labelClsExtra: 'x-form-item-label-te',
                            name: 'description',
                            fieldStyle: 'x-form-field-te'
                        },
                        {
                            xtype: 'hiddenfield',
                            flex: 1,
                            margins: '0 20 0 0 ',
                            labelAlign: 'top',
                            labelClsExtra: 'x-form-item-label-te',
                            name: 'id',
                            value: 0
                        },
                        {
                            xtype: 'hiddenfield',
                            flex: 1,
                            margins: '0 20 0 0 ',
                            labelAlign: 'top',
                            labelClsExtra: 'x-form-item-label-te',
                            name: 'application'
                        }
                    ]
                }
            ],
            dockedItems: [
                {
                    xtype: 'toolbar',
                    dock: 'bottom',
                    ui: 'footer',
                    items: [
                        {
                            xtype: 'button',
                            margin: '0 20',
                            text: 'Save',
                            listeners: {
                                click: {
                                    fn: me.onButtonClick1,
                                    scope: me
                                }
                            }
                        },
                        {
                            xtype: 'button',
                            text: 'Delete',
                            listeners: {
                                click: {
                                    fn: me.onButtonClick11,
                                    scope: me
                                }
                            }
                        },
                        {
                            xtype: 'tbfill'
                        },
                        {
                            xtype: 'button',
                            margin: '0 20',
                            text: 'Close',
                            listeners: {
                                click: {
                                    fn: me.onButtonClick,
                                    scope: me
                                }
                            }
                        }
                    ]
                }
            ]
        });

        me.callParent(arguments);
    },

    onButtonClick1: function(button, e, eOpts) {
        var panel = Ext.getCmp('ShopifyForm');
        var form = panel.getForm();
        if (form.isValid()) {
            panel.setLoading('Saving ... ');
            var id = panel.down('hiddenfield[name="id"]').getValue();
            var url = '';                    
            if ( id > 0) {
                url = '/shopify/webservice/update';
            } else {
                url = '/shopify/webservice/create';
            } 
            form.submit({
                url: url,
                success: function(form,action){
                    panel.setLoading(false);
                    Ext.StoreMgr.lookup('ShopifyShopsTreeStore').reload();
                    button.up('window').close();
                },
                failure: function(form, action){
                    panel.setLoading(false);
                    var msg  = action.result.msg; 
                    Ext.Msg.show({
                        msg: msg,
                        buttons: Ext.Msg.OK,
                        icon: Ext.Msg.WARNING
                    });
                    button.up('window').close();
                }
            });
        }   
    },

    onButtonClick11: function(button, e, eOpts) {
        var panel = Ext.getCmp('ShopifyForm');
        var id = panel.down('hiddenfield[name="id"]').getValue();
        var shopName = panel.down('textfield[name="name"]').getValue();
        if (id > 0) {
            Ext.Msg.show({
                title: 'Delete Shopify Shop?',
                msg: 'Are you sure you want to permanently delete the ' + shopName +' shop?',
                buttons: Ext.Msg.YESNO,
                fn: function(response){
                    if ( response === 'yes') {
                        panel.setLoading('Saving ... ');
                        Ext.Ajax.request({
                            url: '/shopify/webservice/delete',
                            params: { id: id },
                            success: function(){
                                button.up('window').close();
                                Ext.StoreMgr.lookup('ShopifyShopsTreeStore').reload();
                                panel.setLoading(false);
                            },
                            failure: function(){
                                panel.setLoading(false);
                                button.up('window').close();
                            }});
                        }
                    }    
                });
            }
    },

    onButtonClick: function(button, e, eOpts) {
        button.up('window').close();
    }

});